mapper.select:
select * from (
	select rank() over(order by n_idx desc) as no, n.*
	from (select * from news) n
) where no between #{ start } and #{ end }

mapper.update:
update news set
	n_title		= #{n_title},
	n_company	= #{n_company},
	n_content	= #{n_content}
where n_idx = #{n_idx}

update news set
	n_readhit	= n_readhit + 1
where n_idx = #{n_idx}
